package com.leetcode;

import java.util.HashMap;
import java.util.HashSet;
import java.util.Map;
import java.util.Set;

/**
 * Created by xiaoyuan on 11/06/2017.
 */
public class P467_UniqueSubstringsinWraparoundString {

    public int findSubstringInWraproundString(String p) {
        if (p == null || p.length() == 0) {return 0;}

        Map<Character, Integer> maxMap = new HashMap<>();

        int cnt = 1;
        char lastCh = p.charAt(0);
        for (int i = 1; i <= p.length(); i++) {
            if (i == p.length() || (p.charAt(i) - lastCh + 26) % 26 != 1) {

                int begin = i - cnt;
                int end = i - 1;

                for (int  j = begin; j <= Math.min(end, i + 25); j++) {
                    char ch = p.charAt(j);
                    maxMap.put(ch, Math.max(maxMap.getOrDefault(ch, 0), end - j + 1));
                }

                cnt = 1;
                lastCh = i != p.length() ? p.charAt(i) : '#';
            } else {
                cnt++;
                lastCh = p.charAt(i);
            }
        }

        int ans = 0;

        for (int d : maxMap.values()) {
            ans += d;
        }

        return ans;
    }

    public static void main(String[] args) {
        System.out.println(new P467_UniqueSubstringsinWraparoundString().findSubstringInWraproundString("zab"));
        System.out.println(new P467_UniqueSubstringsinWraparoundString().findSubstringInWraproundString("cac"));
        System.out.println(new P467_UniqueSubstringsinWraparoundString().findSubstringInWraproundString(""));
    }

}
