public class Pf161_OneEditDistance {

    public boolean isOneEditDistance(String s, String t) {
        int sp = 0;
        int tp = 0;
        while (true) {
            if (sp == s.length() && tp == t.length()) {return false;}
            if (sp == s.length()) {
                if (tp == t.length() - 1) {return true;}
                return false;
            } else if (tp == t.length()) {
                if (sp == s.length() - 1) {return true;}
                return false;
            } else {
                if (s.charAt(sp) == t.charAt(tp)) {
                    sp ++;
                    tp ++;
                } else {
                    return (s.substring(sp + 1).equals(t.substring(tp + 1))) || (s.substring(sp + 1).equals(t.substring(tp))) || (s.substring(sp).equals(t.substring(tp + 1)));
                }
            }
        }
    }

    public static void main(String[] args) {
        System.out.println(new Pf161_OneEditDistance().isOneEditDistance("",
                ""));
    }

}
